function onLoad()
  self.interactable=false
  self.setPosition({-56.5,-5,0})
  self.clearButtons()
  genButten={
    index=1, label="Generate Mission", click_function="missionGen", function_owner=self,
    position={0,1.3,-0.5}, rotation={0,0,0}, height=800, width=5900,
    font_size=500, color={0,0,0}, font_color={1,1,1},tooltip = "Left Click for no Sudden Death\nRight Click for Sudden Death"
  }
  self.createButton(genButten)
end

function missionGen(obj, player_clicker_color, alt_click)
  self.clearButtons()
  self.createButton(genButten)
  math.randomseed(tonumber(tostring(os.time()):reverse():sub(1,7))+tonumber(tostring(os.clock()):reverse():sub(1,7)))
  rng={}
  rng[1]=math.random(6)
  rng[2]=math.random(12)
  rng[3]=math.random(12)
  rng[4]=math.random(6)
  rng[5]=math.random(6)
  rng1lookup={"Spearhead Assault","Dawn of War","Search and Destroy","Hammer and Anvil","Front-Line Assault","Vanguard Strike"}
  rng2lookup={"Field of Glory","Invasion","War of Attrition","The Prize","Drawn and Quartered","Burn and Pillage","Show of Force","Take and Hold","Glory Seeker","Starfall","Kingslayer","Supply Cache"}
  rng3lookup={"Dead of Night","Witch Hunter","Orbital Debris","Acid Rain","Surprise Attack","Slay the Warlords","Grudge Match","Battle Frenzy","Set Piece Battle","Many Paths to Victory","Double or Nothing","Strategic Escalation"}
  rng4lookup={"Ambush","Dug In","The Blood of Martyrs","Inspired Tactician","Brilliant Strategist","Tactical Reserves"}
  rng5lookup={"Kill Order","Blunt","Endure","Vital Ground","Vendetta","Hold the Line"}

  if alt_click == false then
    Butten0={
      index=1, label="Mission ID# "..string.format("%x",rng[1])..string.format("%x",rng[2])..string.format("%x",rng[3])..string.format("%x",rng[4]), click_function="none", function_owner=self,
      position={0,1.3,1}, rotation={0,0,0}, height=400, width=5900,
      font_size=250, color={0,0,0}, font_color={1,1,1}
    }
  else
    Butten0={
      index=1, label="Mission ID# "..string.format("%x",rng[1])..string.format("%x",rng[2])..string.format("%x",rng[3])..string.format("%x",rng[4])..string.format("%x",rng[5]), click_function="none", function_owner=self,
      position={0,1.3,1}, rotation={0,0,0}, height=400, width=5900,
      font_size=250, color={0,0,0}, font_color={1,1,1}
    }
  end
      Butten1={
        index=1, label="Deployment Zone: #"..rng[1].." "..rng1lookup[rng[1]], click_function="none", function_owner=self,
        position={0,1.3,2}, rotation={0,0,0}, height=400, width=5900,
        font_size=250, color={0,0,0}, font_color={1,1,1}
      }
  Butten2={
    index=2, label="Objective: #"..rng[2].." "..rng2lookup[rng[2]], click_function="none", function_owner=self,
    position={0,1.3,3}, rotation={0,0,0}, height=400, width=5900,
    font_size=250, color={0,0,0}, font_color={1,1,1}
  }
  Butten3={
    index=3, label="Twist: #"..rng[3].." "..rng3lookup[rng[3]], click_function="none", function_owner=self,
    position={0,1.3,4}, rotation={0,0,0}, height=400, width=5900,
    font_size=250, color={0,0,0}, font_color={1,1,1}
  }
  Butten4={
    index=4, label="Ruse: #"..rng[4].." "..rng4lookup[rng[4]], click_function="none", function_owner=self,
    position={0,1.3,5}, rotation={0,0,0}, height=400, width=5900,
    font_size=250, color={0,0,0}, font_color={1,1,1}
  }
  Butten5={
    index=5, label="Sudden Death: #"..rng[5].." "..rng5lookup[rng[5]], click_function="none", function_owner=self,
    position={0,1.3,6}, rotation={0,0,0}, height=400, width=5900,
    font_size=250, color={0,0,0}, font_color={1,1,1}
  }

  self.createButton(Butten0)
  self.createButton(Butten1)
  self.createButton(Butten2)
  self.createButton(Butten3)
  self.createButton(Butten4)

  if alt_click == true then self.createButton(Butten5) end
end

function none()
end

